import axios from 'axios';

const apiUrl = "http://localhost:5188";

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`);    
    return result.data;
  },
  addTask: async (name) => {
    try {
      const result = await axios.post('/task', { name});
      return result.data;
    } catch (error) {
      console.error('Error adding task:', error);
      throw error;
    }
  },
  addTask: async(name)=>{
    console.log('addTask', name);
    try {
      const result = await axios.post(`${apiUrl}/items`, { name: name, isComplete: false });
      return result.data;
    } catch (error) {
      console.error('Error adding task:', error);
      return {};
    }
  },

  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id, isComplete});
    try {
      const result = await axios.put(`${apiUrl}/items/${id}`, { isComplete: isComplete });
      return result.data;
    } catch (error) {
      console.error('Error setting task completion:', error);
      return {};
    }
  },

  deleteTask:async(id)=>{
    console.log('deleteTask', id);
    try {
      const result = await axios.delete(`${apiUrl}/items/${id}`);
      return result.data;
    } catch (error) {
      console.error('Error deleting task:', error);
      return {};
    }
  }
};
